document.addEventListener("DOMContentLoaded", function () {
    var currentPage = 1;
    var rowsPerPage = 10; // Número de filas por página

    // Obtener todas las filas de la tabla
    var tableBody = document.querySelector("table tbody");
    var rows = Array.from(tableBody.getElementsByTagName("tr"));

    // Calcular el número total de páginas
    var totalPages = Math.ceil(rows.length / rowsPerPage);

    // Mostrar las filas de la página actual y ocultar las demás
    function showCurrentPage() {
        var startIndex = (currentPage - 1) * rowsPerPage;
        var endIndex = startIndex + rowsPerPage;

        rows.forEach(function (row, index) {
            if (index >= startIndex && index < endIndex) {
                row.style.display = "table-row";
            } else {
                row.style.display = "none";
            }
        });
    }

    // Actualizar los botones de navegación de página
    function updatePageButtons() {
        var prevButton = document.getElementById("prev-page-btn");
        var nextButton = document.getElementById("next-page-btn");

        prevButton.disabled = (currentPage === 1);
        nextButton.disabled = (currentPage === totalPages);
    }

    // Ir a la página anterior
    function goToPreviousPage() {
        if (currentPage > 1) {
            currentPage--;
            showCurrentPage();
            updatePageButtons();
        }
    }

    // Ir a la página siguiente
    function goToNextPage() {
        if (currentPage < totalPages) {
            currentPage++;
            showCurrentPage();
            updatePageButtons();
        }
    }

    // Asignar los controladores de eventos a los botones de página
    var prevButton = document.getElementById("prev-page-btn");
    var nextButton = document.getElementById("next-page-btn");

    prevButton.addEventListener("click", goToPreviousPage);
    nextButton.addEventListener("click", goToNextPage);

    // Mostrar la página inicial y actualizar los botones de página
    showCurrentPage();
    updatePageButtons();
});
